select count(DISTINCT namespace) as "命名空间"
  FROM
    kubedoor.${table} PREWHERE date = '${maxday}'AND ${env}

select count(DISTINCT deployment) as "微服务"
  FROM
    kubedoor.${table} PREWHERE date = '${maxday}'AND ${env}

select sum(pod_count) as "Pod"
  FROM
    kubedoor.${table} PREWHERE date = '${maxday}'AND ${env}

select sum(request_pod_cpu_m * pod_count)/1000 as "CPU需求"
  FROM
    kubedoor.${table} PREWHERE date = '${maxday}'AND ${env}

select sum(p95_pod_load * pod_count) as "负载使用"
  FROM
    kubedoor.${table} PREWHERE date = '${maxday}'AND ${env}

select sum(limit_pod_cpu_m * pod_count)/1000 as "CPU限制"
  FROM
    kubedoor.${table} PREWHERE date = '${maxday}'AND ${env}

select sum(request_pod_mem_mb * pod_count) as "内存需求"
